def bda_link_mapping(name, dict=None):
    acc_str = "http://130.238.65.211:9092/files/BDA/pdf/"
    name_parts = name.split("_")
    year = name_parts[3]
    
    if name in files_from_1954:
        year = "1954"
    elif name in files_from_1955:
        year = "1955"
        
    if year in year_replacements:
        print(year)
        year = year_replacements[year]
        
    acc_str = acc_str + year + "/" + name.replace(".txt", ".pdf")
    return acc_str

